// src/main/index.js
import React, { useState } from 'react';
import { Button, CircularProgress, Container, Typography, Paper } from '@mui/material';

import './style.css'; // Import your styles

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [downloadMessage, setDownloadMessage] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a video file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setProcessedVideo(data.processed_video_path);
      setDownloadMessage('');
    } catch (error) {
      console.error('Error uploading video:', error);
      alert('Error uploading video. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async () => {
    if (processedVideo) {
      // Implement download logic
      // You may want to use the processedVideo link from the backend
      // For simplicity, it assumes a direct download link
      try {
        const response = await fetch(`http://localhost:5000/download/${processedVideo}`);
        const message = await response.text();
        setDownloadMessage(message);
      } catch (error) {
        console.error('Error downloading video:', error);
        alert('Error downloading video. Please try again.');
      }
    } else {
      alert('No processed video available for download.');
    }
  };

  return (
    <Container className="container">
      <Typography variant="h4" align="center" gutterBottom>
      Bigbuddy AI
      </Typography>
      <Paper className="videoContainer">
        <input type="file" accept="video/*" onChange={handleFileChange} />
        <Button variant="contained" color="primary" onClick={handleUpload}>
          Upload
        </Button>
        {loading && <CircularProgress className="progress" />}
        {processedVideo && (
          <>
            <video controls className="videoPlayer">
              <source src={`http://127.0.0.1:5000/process_video`} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            {downloadMessage && (
              <Typography variant="body1" className="downloadMessage">
                {downloadMessage}
              </Typography>
            )}
            <Button
              variant="contained"
              color="primary"
              className="downloadButton"
              onClick={handleDownload}
            >
              Download Processed Video
            </Button>
          </>
        )}
      </Paper>
    </Container>
  );
};

export default App;
