import React, { useState } from 'react';
import { Button, CircularProgress, Container, Typography, makeStyles, Paper } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
  videoContainer: {
    marginTop: theme.spacing(2),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  videoPlayer: {
    width: '100%',
    marginTop: theme.spacing(2),
  },
  downloadButton: {
    marginTop: theme.spacing(2),
  },
  progress: {
    marginTop: theme.spacing(2),
  },
}));

const App = () => {
  const classes = useStyles();
  const [selectedFile, setSelectedFile] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a video file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setProcessedVideo(data.processed_video_path);
    } catch (error) {
      console.error('Error uploading video:', error);
      alert('Error uploading video. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    // Implement download logic
    // You may want to use the processedVideo link from the backend
    // For simplicity, it assumes a direct download link
    window.open(`http://localhost:5000/download/${processedVideo}`, '_blank');
  };

  return (
    <Container className={classes.container}>
      <Typography variant="h4" align="center" gutterBottom>
        Video Processing App
      </Typography>
      <Paper className={classes.videoContainer}>
        <input type="file" accept="video/*" onChange={handleFileChange} />
        <Button variant="contained" color="primary" onClick={handleUpload}>
          Upload
        </Button>
        {loading && <CircularProgress className={classes.progress} />}
        {processedVideo && (
          <>
            <video controls className={classes.videoPlayer}>
              <source src={`http://localhost:5000/${processedVideo}`} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            <Button
              variant="contained"
              color="primary"
              className={classes.downloadButton}
              onClick={handleDownload}
            >
              Download Processed Video
            </Button>
          </>
        )}
      </Paper>
    </Container>
  );
};

export default App;
